// Resume Template Generation
export function generateResumeTemplate(data) {
    return "\n        <header>\n            <div class=\"header-content\">\n                <img id=\"resumePicture\" src=\"".concat(data.picture || '../../public/default-profile.jpg', "\" alt=\"Profile Picture\">\n                <div class=\"header-text\">\n                    <h1 id=\"resumeName\" data-field=\"name\">").concat(data.name, "</h1>\n                    <p id=\"resumeProfession\" data-field=\"profession\">").concat(data.profession, "</p>\n                </div>\n            </div>\n        </header>\n\n        <main>\n            <section class=\"contact\">\n                <ul>\n                    <li><i class=\"fas fa-envelope\"></i> <span id=\"resumeEmail\" data-field=\"email\">").concat(data.email, "</span></li>\n                    <li><i class=\"fas fa-phone\"></i> <span id=\"resumePhone\" data-field=\"phone\">").concat(data.phone, "</span></li>\n                    <li><i class=\"fas fa-map-marker-alt\"></i> <span id=\"resumeLocation\" data-field=\"location\">").concat(data.location, "</span></li>\n                    <li><i class=\"fas fa-globe\"></i> <a id=\"resumeWebsite\" data-field=\"website\" href=\"").concat(data.website, "\">").concat(data.website, "</a></li>\n                </ul>\n            </section>\n\n            <section class=\"summary\">\n                <h2>Professional Summary</h2>\n                <p id=\"resumeSummary\" data-field=\"summary\">").concat(data.summary, "</p>\n            </section>\n\n            <section class=\"experience\">\n                <h2>Work Experience</h2>\n                ").concat(data.workExperience.map(function (job, index) { return generateJobHTML(job, index); }).join(''), "\n            </section>\n\n            <section class=\"education\">\n                <h2>Education</h2>\n                <div class=\"degree\">\n                    <h3 data-field=\"education.degree\">").concat(data.education.degree, "</h3>\n                    <p class=\"school\" data-field=\"education.school\">").concat(data.education.school, "</p>\n                    <p class=\"date\" data-field=\"education.date\">").concat(data.education.date, "</p>\n                </div>\n            </section>\n\n            <section class=\"skills\">\n                <h2>Skills</h2>\n                <ul>\n                    ").concat(data.skills.map(function (skill, index) { return "\n                        <li data-field=\"skills[".concat(index, "]\">").concat(skill, "</li>\n                    "); }).join(''), "\n                </ul>\n            </section>\n        </main>\n    ");
}
function generateJobHTML(job, index) {
    return "\n        <div class=\"job\" id=\"job-".concat(index, "\">\n            <div class=\"job-header\">\n                <h3 data-field=\"workExperience[").concat(index, "].title\">").concat(job.title, "</h3>\n                <p class=\"company\" data-field=\"workExperience[").concat(index, "].company\">").concat(job.company, "</p>\n            </div>\n            <p class=\"date\" data-field=\"workExperience[").concat(index, "].date\">").concat(job.date, "</p>\n            <ul>\n                ").concat(job.responsibilities.map(function (responsibility, rIndex) { return "\n                    <li data-field=\"workExperience[".concat(index, "].responsibilities[").concat(rIndex, "]\">").concat(responsibility, "</li>\n                "); }).join(''), "\n            </ul>\n        </div>\n    ");
}
// Global Resume Data
export var template2 = function (data) {
    return "\n    <div class=\"resume-template-2\">\n        <header class=\"header-content\">\n            <img id=\"resumePicture\" src=\"".concat(data.picture || '../../public/default-profile.jpg', "\" alt=\"Profile Picture\">\n            <div class=\"header-text\">\n                <h1 id=\"resumeName\" data-field=\"name\" contenteditable=\"true\">").concat(data.name, "</h1>\n                <p id=\"resumeProfession\" data-field=\"profession\" contenteditable=\"true\">").concat(data.profession, "</p>\n            </div>\n        </header>\n\n        <main>\n            <div class=\"content-template-2\">\n                <div class=\"left-column-template-2\">\n                    <section class=\"contact-info-template-2\">\n                        <h2>Contact</h2>\n                        <ul>\n                            <li><i class=\"fas fa-envelope\"></i> <span id=\"resumeEmail\" data-field=\"email\" contenteditable=\"true\">").concat(data.email, "</span></li>\n                            <li><i class=\"fas fa-phone\"></i> <span id=\"resumePhone\" data-field=\"phone\" contenteditable=\"true\">").concat(data.phone, "</span></li>\n                            <li><i class=\"fas fa-map-marker-alt\"></i> <span id=\"resumeLocation\" data-field=\"location\" contenteditable=\"true\">").concat(data.location, "</span></li>\n                            <li><i class=\"fas fa-globe\"></i> <a id=\"resumeWebsite\" data-field=\"website\" href=\"").concat(data.website, "\" contenteditable=\"true\">").concat(data.website, "</a></li>\n                        </ul>\n                    </section>\n                    <section class=\"section-template-2\">\n                        <h2>Skills</h2>\n                        <div class=\"skills-list-template-2\">\n                            ").concat(data.skills.map(function (skill, index) { return "\n                                <span class=\"skill-template-2\" data-field=\"skills[".concat(index, "]\" contenteditable=\"true\">").concat(skill, "</span>\n                            "); }).join(''), "\n                        </div>\n                    </section>\n                </div>\n                <div class=\"right-column-template-2\">\n                    <section class=\"section-template-2\">\n                        <h2>Professional Summary</h2>\n                        <p id=\"resumeSummary\" data-field=\"summary\" contenteditable=\"true\">").concat(data.summary, "</p>\n                    </section>\n                    <section class=\"section-template-2\">\n                        <h2>Work Experience</h2>\n                        ").concat(data.workExperience.map(function (job, index) { return "\n                            <div class=\"job-template-2\" id=\"job-".concat(index, "\">\n                                <div class=\"job-header-template-2\">\n                                    <h3 data-field=\"workExperience[").concat(index, "].title\" contenteditable=\"true\">").concat(job.title, "</h3>\n                                    <p class=\"company-template-2\" data-field=\"workExperience[").concat(index, "].company\" contenteditable=\"true\">").concat(job.company, "</p>\n                                </div>\n                                <p class=\"date-template-2\" data-field=\"workExperience[").concat(index, "].date\" contenteditable=\"true\">").concat(job.date, "</p>\n                                <ul>\n                                    ").concat(job.responsibilities.map(function (responsibility, rIndex) { return "\n                                        <li data-field=\"workExperience[".concat(index, "].responsibilities[").concat(rIndex, "]\" contenteditable=\"true\">").concat(responsibility, "</li>\n                                    "); }).join(''), "\n                                </ul>\n                            </div>\n                        "); }).join(''), "\n                    </section>\n                    <section class=\"section-template-2\">\n                        <h2>Education</h2>\n                        <div class=\"degree-template-2\">\n                            <h3 data-field=\"education.degree\" contenteditable=\"true\">").concat(data.education.degree, "</h3>\n                            <p class=\"school-template-2\" data-field=\"education.school\" contenteditable=\"true\">").concat(data.education.school, "</p>\n                            <p class=\"date-template-2\" data-field=\"education.date\" contenteditable=\"true\">").concat(data.education.date, "</p>\n                        </div>\n                    </section>\n                </div>\n            </div>\n        </main>\n    </div>\n    ");
};
