export var template4 = function (data) {
    return "\n    <div class=\"resume-template-4\">\n        <header class=\"header-content\">\n            <img id=\"resumePicture\" src=\"".concat(data.picture || '../../public/default-profile.jpg', "\" alt=\"Profile Picture\">\n            <div class=\"header-text\">\n                <h1 id=\"resumeName\" data-field=\"name\">").concat(data.name, "</h1>\n                <p id=\"resumeProfession\" data-field=\"profession\">").concat(data.profession, "</p>\n            </div>\n        </header>\n\n        <main>\n            <section class=\"overview\">\n                <h2>Overview</h2>\n                <p id=\"resumeSummary\" data-field=\"summary\">").concat(data.summary, "</p>\n            </section>\n\n            <section class=\"experience\">\n                <h2>Experience</h2>\n                ").concat(data.workExperience.map(function (job, index) { return generateJobHTML(job, index); }).join(''), "\n            </section>\n\n            <section class=\"education\">\n                <h2>Education</h2>\n                <div class=\"degree\">\n                    <h3 data-field=\"education.degree\">").concat(data.education.degree, "</h3>\n                    <p class=\"school\" data-field=\"education.school\">").concat(data.education.school, "</p>\n                    <p class=\"date\" data-field=\"education.date\">").concat(data.education.date, "</p>\n                </div>\n            </section>\n\n            <section class=\"skills\">\n                <h2>Skills</h2>\n                <div class=\"skills-grid\">\n                    ").concat(data.skills.map(function (skill, index) { return "\n                        <div class=\"skill-item\" data-field=\"skills[".concat(index, "]\">").concat(skill, "</div>\n                    "); }).join(''), "\n                </div>\n            </section>\n        </main>\n    </div>\n    ");
};
