// Resume Template Generation
export function generateResumeTemplate(data) {
    return "\n        <header>\n            <div class=\"header-content\">\n                <img id=\"resumePicture\" src=\"".concat(data.picture || '../../public/default-profile.jpg', "\" alt=\"Profile Picture\">\n                <div class=\"header-text\">\n                    <h1 id=\"resumeName\" data-field=\"name\">").concat(data.name, "</h1>\n                    <p id=\"resumeProfession\" data-field=\"profession\">").concat(data.profession, "</p>\n                </div>\n            </div>\n        </header>\n\n        <main>\n            <section class=\"contact\">\n                <ul>\n                    <li><i class=\"fas fa-envelope\"></i> <span id=\"resumeEmail\" data-field=\"email\">").concat(data.email, "</span></li>\n                    <li><i class=\"fas fa-phone\"></i> <span id=\"resumePhone\" data-field=\"phone\">").concat(data.phone, "</span></li>\n                    <li><i class=\"fas fa-map-marker-alt\"></i> <span id=\"resumeLocation\" data-field=\"location\">").concat(data.location, "</span></li>\n                    <li><i class=\"fas fa-globe\"></i> <a id=\"resumeWebsite\" data-field=\"website\" href=\"").concat(data.website, "\">").concat(data.website, "</a></li>\n                </ul>\n            </section>\n\n            <section class=\"summary\">\n                <h2>Professional Summary</h2>\n                <p id=\"resumeSummary\" data-field=\"summary\">").concat(data.summary, "</p>\n            </section>\n\n            <section class=\"experience\">\n                <h2>Work Experience</h2>\n                ").concat(data.workExperience.map(function (job, index) { return generateJobHTML(job, index); }).join(''), "\n            </section>\n\n            <section class=\"education\">\n                <h2>Education</h2>\n                <div class=\"degree\">\n                    <h3 data-field=\"education.degree\">").concat(data.education.degree, "</h3>\n                    <p class=\"school\" data-field=\"education.school\">").concat(data.education.school, "</p>\n                    <p class=\"date\" data-field=\"education.date\">").concat(data.education.date, "</p>\n                </div>\n            </section>\n\n            <section class=\"skills\">\n                <h2>Skills</h2>\n                <ul>\n                    ").concat(data.skills.map(function (skill, index) { return "\n                        <li data-field=\"skills[".concat(index, "]\">").concat(skill, "</li>\n                    "); }).join(''), "\n                </ul>\n            </section>\n        </main>\n    ");
}
function generateJobHTML(job, index) {
    return "\n        <div class=\"job\" id=\"job-".concat(index, "\">\n            <div class=\"job-header\">\n                <h3 data-field=\"workExperience[").concat(index, "].title\">").concat(job.title, "</h3>\n                <p class=\"company\" data-field=\"workExperience[").concat(index, "].company\">").concat(job.company, "</p>\n            </div>\n            <p class=\"date\" data-field=\"workExperience[").concat(index, "].date\">").concat(job.date, "</p>\n            <ul>\n                ").concat(job.responsibilities.map(function (responsibility, rIndex) { return "\n                    <li data-field=\"workExperience[".concat(index, "].responsibilities[").concat(rIndex, "]\">").concat(responsibility, "</li>\n                "); }).join(''), "\n            </ul>\n        </div>\n    ");
}
// Global Resume Data
