// Function to generate a resume template
export function generateResumeTemplate(data) {
    return "\n    <div class=\"container\" id=\"resume\">\n        <header>\n            <div class=\"header-content\">\n                <img id=\"resumePicture\" src=\"".concat(data.picture || 'default-profile.png', "\" alt=\"Profile Picture\">\n                <h1 id=\"resumeName\">").concat(data.name, "</h1>\n                <p id=\"resumeProfession\">").concat(data.profession, "</p>\n            </div>\n        </header>\n\n        <main>\n            <section class=\"contact\">\n                <ul>\n                    <li><i class=\"fas fa-envelope\"></i><span id=\"resumeEmail\">").concat(data.email, "</span></li>\n                    <li><i class=\"fas fa-phone\"></i><span id=\"resumePhone\">").concat(data.phone, "</span></li>\n                    <li><i class=\"fas fa-map-marker-alt\"></i><span id=\"resumeLocation\">").concat(data.location, "</span></li>\n                    <li><i class=\"fas fa-globe\"></i><a id=\"resumeWebsite\" href=\"").concat(data.website, "\">").concat(data.website, "</a></li>\n                </ul>\n            </section>\n\n            <section class=\"summary\">\n                <h2>Professional Summary</h2>\n                <p id=\"resumeSummary\">").concat(data.summary, "</p>\n            </section>\n\n            <section class=\"experience\">\n                <h2>Work Experience</h2>\n                ").concat(data.workExperience.map(function (job, index) { return "\n                    <div class=\"job\" id=\"job-".concat(index, "\">\n                        <div class=\"job-header\">\n                            <h3>").concat(job.title, "</h3>\n                            <p class=\"company\">").concat(job.company, "</p>\n                        </div>\n                        <p class=\"date\">").concat(job.date, "</p>\n                        <ul>\n                            ").concat(job.responsibilities.map(function (responsibility) { return "<li>".concat(responsibility, "</li>"); }).join(''), "\n                        </ul>\n                    </div>\n                "); }).join(''), "\n            </section>\n\n            <section class=\"education\">\n                <h2>Education</h2>\n                    <div class=\"degree\">\n                        <h3>").concat(data.education.degree, "</h3>\n                        <p class=\"school\">").concat(data.education.school, "</p>\n                        <p class=\"date\">").concat(data.education.date, "</p>\n                    </div>\n            </section>\n\n            <section class=\"skills\">\n                <h2>Skills</h2>\n                <ul>\n                    ").concat(data.skills.map(function (skill) { return "<li>".concat(skill, "</li>"); }).join(''), "\n                </ul>\n            </section>\n        </main>\n    </div>\n    ");
}
